CREATE TABLE EA.DISCIPLINE (
    ID   NUMBER(6,0) PRIMARY KEY,
    CODE VARCHAR2(2 BYTE),
    NAME VARCHAR2(10 BYTE)
);

CREATE TABLE EA.FIELD_CLASS (
    ID            NUMBER(8,0) PRIMARY KEY,
    DISCIPLINE_ID NUMBER(6,0) REFERENCES EA.DISCIPLINE(ID),
    CODE          VARCHAR2(4 BYTE),
    NAME          VARCHAR2(20 BYTE)
);

CREATE TABLE EA.FIELD (
    ID             NUMBER(10,0) PRIMARY KEY,
    FIELD_CLASS_ID NUMBER(8,0) REFERENCES EA.FIELD_CLASS(ID),
    CODE           VARCHAR2(6 BYTE),
    NAME           VARCHAR2(50 BYTE),
    FLAG           VARCHAR2(2 BYTE)
);

CREATE TABLE EA.FIELD_ALLOW_DEGREE (
    FIELD_ID      NUMBER(10,0) REFERENCES EA.FIELD(ID),
    DISCIPLINE_ID NUMBER(6,0) REFERENCES EA.DISCIPLINE(ID)
);

CREATE TABLE EA.TERM (
    ID         NUMBER(5,0) PRIMARY KEY,
    START_DATE DATE,
    START_WEEK NUMBER(2,0),
    MID_LEFT   NUMBER(2,0),
    MID_RIGHT  NUMBER(2,0),
    END_WEEK   NUMBER(2,0),
    MAX_WEEK   NUMBER(2,0)
);

CREATE TABLE EA.COURSE_CLASS_MAPPING (
    COURSE_CLASS_ID NUMBER(13,0) PRIMARY KEY,
    ORIGINAL_ID     VARCHAR2(31 BYTE) UNIQUE NOT NULL,
    DATE_CREATED    TIMESTAMP NOT NULL
);





